let right_now=new Date,times=[["second",1],["minute",60],["hour",3600],["day",86400],["week",604800],["month",2592e3],["year",31536e3]];function formatTime(e){e=new Date(e);for(var t=Math.round((right_now-e)/1e3),a=0;a<times.length;a++)if(t<times[a][1])return 0==a?"Just now":(t=Math.round(t/times[a-1][1]))+" "+times[a-1][0]+(1==t?" ago":"s ago")}function makeCard(e){let t=new XMLHttpRequest,a="https://api.github.com/repos/"+e;t.open("GET",a,!0),t.onload=function(){let t=JSON.parse(this.response),a=`<div class="repo-box">\n\t<div class="repo-title">\n\t\t<a class="repo-author" href="${t.owner.html_url}">${t.owner.login}</a> / <a class="repo-name" href="${t.html_url}">${t.name}</a>\n\t</div>\n\t<a href="${t.html_url}" class="repo-meta">\n\t\t<span class="repo-stars">${t.stargazers_count} stars</span>\n\t\t<span class="repo-license">MIT</span>\n\t\t<span class="repo-updated">${formatTime(t.pushed_at)}</span>\n\t</a>\n</div>`;document.querySelector(`[data-repo="${e}"]`).innerHTML=a},t.send()}document.querySelectorAll("[data-repo]").forEach((e=>{makeCard(e.dataset.repo)}));